import { Component, OnInit } from '@angular/core';
import { NgForm} from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { StudentService } from 'src/app/shared/student.service';

@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: []
})
export class StudentComponent implements OnInit{
  ProfileImg : string = '../../../assets/image/ProfilePicture.png';
  isValid : boolean = true;

  constructor(public studentSvc:StudentService, private currentRoute:ActivatedRoute){}

  ngOnInit(): void {
    let studentID = this.currentRoute.snapshot.paramMap.get('id');
    if(studentID == null)
    this.resetForm();
    else

  }

  resetForm(form?:NgForm | any){
    if(form = null)
    form.resetForm();
    this.studentSvc.studenytData ={
      StudentID : null,
      FirstName : '',
      LastName : '',
      Mobile : '',
      Email : '',
      NIC : '',
      Address : '',
      DateOfBirth : null

    };
  }

  ValidateForm(){
    this.isValid = true;
    if(this.studentSvc.studenytData.FirstName == ''){
      this.isValid = false;
    }

      if(this.studentSvc.studenytData.LastName == ''){
        this.isValid = false;
    }

    if(this.studentSvc.studenytData.Mobile == ''){
      this.isValid = false;
    }

      if(this.studentSvc.studenytData.Email == ''){
        this.isValid = false;
    }

    if(this.studentSvc.studenytData.NIC == ''){
      this.isValid = false;
    }

      if(this.studentSvc.studenytData.Address == ''){
        this.isValid = false;
    }

    if(this.studentSvc.studenytData.DateOfBirth == null){
      this.isValid = false;
    }

    return this.isValid;
  }

  onSubmit(form:NgForm){
    if(this.ValidateForm()){
      this.studentSvc.saveOrUpdateUser().subscribe(res =>{
        this.resetForm();
      })
    }
  }

}
<form #form="ngForm" autocomplete="off" *ngIf="studentSvc.studenytData" (submit)="onSubmit(form)">
    <img [src]="ProfileImg" class="rounded mx-auto d-block" style="border-style: solid; border-color: black;margin-bottom: 20px;">
    <div class="row">
        <div class="col-md-6">
            <div class="form-group">
                <label>First Name</label>
                <input name="FirstName" #FirstName="ngModel" [(ngModel)]="studentSvc.studenytData.FirstName" class="form-control"
                [class.is-invalid] = "!isValid && studentSvc.studenytData.FirstName == '' ">
            </div>
        </div>
        <div class="col-md-6">
            <div class="form-group">
                <label>Last Name</label>
                <input name="LastName" #LastName="ngModel" [(ngModel)]="studentSvc.studenytData.LastName" class="form-control"
                [class.is-invalid] = "!isValid && studentSvc.studenytData.LastName == '' ">
            </div>
        </div>
    </div>
    <div class="row">
        <div class="col-md-6">
            <div class="form-group">
                <label>Mobile</label>
                <input name="Mobile" #Mobile="ngModel" [(ngModel)]="studentSvc.studenytData.Mobile" class="form-control"
                [class.is-invalid] = "!isValid && studentSvc.studenytData.Mobile == '' ">
            </div>
        </div>
        <div class="col-md-6">
            <div class="form-group">
                <label>Email</label>
                <input name="Email" #Email="ngModel" [(ngModel)]="studentSvc.studenytData.Email" class="form-control"
                [class.is-invalid] = "!isValid && studentSvc.studenytData.Email == '' ">
            </div>
        </div>
    </div>
    <div class="row">
        <div class="col-md-6">
            <div class="form-group">
                <label>NIC</label>
                <input name="NIC" #NIC="ngModel" [(ngModel)]="studentSvc.studenytData.NIC" class="form-control"
                [class.is-invalid] = "!isValid && studentSvc.studenytData.NIC == '' ">
            </div>
        </div>
        <div class="col-md-4">
            <div class="form-group">
                <label>Date Of Birth</label>
                <input type="date" name="DateOfBirth" #NIC="ngModel" [(ngModel)]="studentSvc.studenytData.DateOfBirth" class="form-control"
                [class.is-invalid] = "!isValid && studentSvc.studenytData.DateOfBirth == null ">
            </div>
        </div>
    </div>
    <div class="row">
        <div class="col-md-12">
            <div class="form-group">
                <label>Address</label>
                <input name="Address" #Address="ngModel" [(ngModel)]="studentSvc.studenytData.Address" class="form-control"
                [class.is-invalid] = "!isValid && studentSvc.studenytData.Address == '' ">
            </div>
        </div>
    </div>

    <div class="form-group">
        <button type="submit" class="btn btn-dark"><i class="fa fa-database"></i> Submit</button>
    </div>
</form>
