import { Component, OnInit } from '@angular/core';
import { StudentService } from '../shared/student.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-students',
  templateUrl: './students.component.html',
  styleUrls: ['./students.component.css']
})
export class StudentsComponent implements OnInit {
  studentList:any;
  showSummary:boolean = false;
  StuID : number = 0;

  constructor(private StudentSvc:StudentService, private route:Router){}

  ngOnInit(): void {
    this.StudentSvc.getStudentList().then(res => this.studentList = res);
  }

  selectStudent(){
    debugger
    // if(ID==this.StuID){
      this.showSummary = !this.showSummary
      console.log(this.showSummary);

   }

   openForEdit(ID:number){
    debugger
    this.route.navigate(['/student/edit/' + ID]);
   }

}

<div class="form-group">
    <button type="submit" class="btn btn-dark"  style="margin-right: 10px;" routerLink="/student"><i class="fa fa-plus-circle"></i></button>

</div>
<div class="row">
<div class="col-md-12">
<table class="table table-hover">
    <thead class="thead-light">
        <th>First Name</th>
        <th>Last Name</th>
        <th>Mobile</th>
        <th>Email</th>
        <th>NIC</th>
        <th>Date Of Birth</th>
        <th>Address</th>
        <th></th>
        <th></th>
    </thead>

    <tbody>
        <tr *ngFor="let item of studentList">
            <td hidden="true">{{item.StudentID}}</td>
            <td>{{item.FirstName}}</td>
            <td>{{item.LastName}}</td>
            <td>{{item.Mobile}}</td>
            <td>{{item.Email}}</td>
            <td>{{item.NIC}}</td>
            <td>{{item.DateOfBirth}}</td>
            <td>{{item.Address}}</td>
            <td>
                <button type="submit" class="btn btn-dark" style="margin-right: 10px;" (click)="openForEdit(item.StudentID)"><i class="fa fa-pencil"></i></button>

            </td>
            <td>
                <button type="submit" class="btn btn-dark" style="margin-right: 10px;"><i class="fa fa-trash-o"></i></button>
            </td>
        </tr>
    </tbody>
</table>
</div>
<div class="col-md-3" [hidden]="showSummary">

    <!-- <app-studentsummary></app-studentsummary> -->

</div>
</div>
